import firebase from 'firebase/compat/app';
import * as i0 from "@angular/core";
import * as i1 from "@angular/fire/compat/auth";
import * as i2 from "../consultas.service";
export class AuthService {
    constructor(afauth, ajax) {
        this.afauth = afauth;
        this.ajax = ajax;
    }
    async login(email, pass) {
        try {
            return await this.afauth.signInWithEmailAndPassword(email, pass);
        }
        catch (err) {
            console.log("Error en el login", err);
            return null;
        }
    }
    async loginWithGoogle() {
        try {
            return await this.afauth.signInWithPopup(new firebase.auth.GoogleAuthProvider());
        }
        catch (err) {
            console.log("Error en el login de google", err);
            return null;
        }
    }
    getUserLogged() {
        return this.afauth.authState;
    }
    logOut() {
        this.afauth.signOut();
        this.ajax.deleteData();
    }
}
